// src/pages/Dashboard.js
import React, { useEffect } from 'react';
import './Dashboard.css';
import { Calendar } from '@fullcalendar/core';
import dayGridPlugin from '@fullcalendar/daygrid';

const Dashboard = ({ recentDocuments, favoriteDocuments, sharedDocuments, notifications, userTasks }) => {
  useEffect(() => {
    var calendarEl = document.getElementById('calendar');
    var calendar = new Calendar(calendarEl, {
      plugins: [dayGridPlugin],
      initialView: 'dayGridMonth',
      events: userTasks.map(task => ({
        title: task.description,
        start: task.due_date
      }))
    });
    calendar.render();
  }, [userTasks]);

  return (
    <div className="dashboard-container">
      <div className="welcome-message">
        <h1>Bienvenid@ al Centro Virtual de Documentación</h1>
        <p>Hola, nombre_usuario. Aquí tiene accesos rápidos a las funcionalidades principales.</p>
      </div>
      <div className="card">
        <h2>Documentos Recientes</h2>
        <ul>
          {recentDocuments.map(document => (
            <li key={document.filename}><a href={`/document/view_file/${document.filename}`}>{document.filename}</a></li>
          ))}
        </ul>
      </div>

      <div className="card">
        <h2>Documentos Favoritos</h2>
        <ul>
          {favoriteDocuments.map(document => (
            <li key={document.filename}><a href={`/document/view_file/${document.filename}`}>{document.filename}</a></li>
          ))}
        </ul>
      </div>

      <div className="card">
        <h2>Documentos Compartidos</h2>
        <ul>
          {sharedDocuments.map(document => (
            <li key={document.filename}><a href={`/document/view_file/${document.filename}`}>{document.filename}</a> - Compartido por: {document.shared_by}</li>
          ))}
        </ul>
      </div>

      <div className="card">
        <h2>Notificaciones</h2>
        <ul>
          {notifications.map(notification => (
            <li key={notification.id}>
              {notification.message} - 
              <a href={`/document/view_file/${notification.filename}`} className="notification-link" data-id={notification.id} data-filename={notification.filename} data-is-read={notification.is_read}>Ver archivo</a> - 
              {notification.date}
            </li>
          ))}
        </ul>
      </div>

      <div className="card">
        <h2>Tareas Pendientes</h2>
        <ul>
          {userTasks.map(task => (
            <li key={task.id}>{task.description} - Fecha límite: {task.due_date}</li>
          ))}
        </ul>
        <form method="post" action="/dashboard/add_task">
          <input type="text" name="task_description" placeholder="Nueva tarea" required />
          <input type="date" name="task_due_date" required />
          <button type="submit" className="submit-button">Añadir</button>
        </form>
      </div>

      <div className="card">
        <h2>Calendario de Actividades</h2>
        <div id="calendar"></div>
      </div>

      <div className="card">
        <h2>Comentarios y Retroalimentación</h2>
        <form method="post" enctype="multipart/form-data" action="/ayuda/submits_feedback">
          <textarea name="feedback" placeholder="Escribe tus comentarios aquí..." required></textarea>
          <label htmlFor="capture" className="form-label">Subir una captura (opcional):</label>
          <input type="file" name="capture" id="capture" accept="image/*" />
          <button type="submit" className="submit-button">Enviar</button>
        </form>
      </div>

      <div id="notificationModal" className="modal">
        <div className="modal-content">
          <span className="close">&times;</span>
          <p id="modalMessage"></p>
          <div style={{ textAlign: 'center' }}><a href="#" id="viewFileButton" className="modal-button">Ver Archivo</a></div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
