import React, { useEffect } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import './Login.css';

const Login = () => {
  useEffect(() => {
    document.body.classList.add('login-page');
    return () => {
      document.body.classList.remove('login-page');
    };
  }, []);

  const handleGoogleSuccess = (response) => {
    console.log(response);
    const token = response.credential;

    fetch('/api/auth/google-login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
    .then((response) => response.json())
    .then((data) => {
      if (data.success) {
        window.location.href = '/dashboard';
      } else {
        alert('Error al iniciar sesión con Google');
      }
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  };

  const handleGoogleFailure = (error) => {
    console.error('Error:', error);
    alert('Error al iniciar sesión con Google');
  };

  return (
    <GoogleOAuthProvider clientId="567687948406-td61t72e79hac1pfl5sg6rm7hbt9ir7d.apps.googleusercontent.com">
      <div className="login-container">
        <img src="images/logo.webp" alt="Logo de la Organización" />
        <h1>Bienvenid@ al Portal</h1>
        <p>Inicio de Sesión</p>
        <form method="POST" action="/login">
          <input type="text" name="username" placeholder="Usuario" required />
          <input type="password" name="password" placeholder="Contraseña" required />
          <button type="submit">Iniciar Sesión</button>
        </form>
        <GoogleLogin
          onSuccess={handleGoogleSuccess}
          onFailure={handleGoogleFailure}
        />
        <p><a href="#">¿Olvidó su contraseña?</a></p>
      </div>
    </GoogleOAuthProvider>
  );
};

export default Login;
