import React, { useState, useEffect } from 'react';
import './UserPermissions.css';

const UserModal = ({ user, permissions = [], closeModal }) => {
    const [selectedPermissions, setSelectedPermissions] = useState(user.user?.permissions?.map(permission => permission.id) || []);

    useEffect(() => {
        // Actualiza el estado si el usuario cambia
        setSelectedPermissions(user.user?.permissions?.map(permission => permission.id) || []);
    }, [user]);

    const handlePermissionChange = (permissionId) => {
        if (selectedPermissions.includes(permissionId)) {
            // Si el permiso ya está seleccionado, lo deselecciona
            setSelectedPermissions(selectedPermissions.filter(id => id !== permissionId));
        } else {
            // Si el permiso no está seleccionado, lo selecciona
            setSelectedPermissions([...selectedPermissions, permissionId]);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Aquí puedes implementar la lógica para actualizar los permisos del usuario
            const currentPermissions = user.user.permissions.map(permission => permission.id);

            // Permisos para asignar
            const permissionsToAssign = selectedPermissions.filter(id => !currentPermissions.includes(id));
            // Permisos para revocar
            const permissionsToRevoke = currentPermissions.filter(id => !selectedPermissions.includes(id));

            // Realizar peticiones para asignar permisos
            for (const permissionId of permissionsToAssign) {
                await fetch('http://fya-api.com:80/assign-permission', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        user_id: user.id,
                        permission_id: permissionId,
                    }),
                });
            }

            // Realizar peticiones para revocar permisos
            for (const permissionId of permissionsToRevoke) {
                await fetch('http://fya-api.com:80/revoke-permission', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        user_id: user.id,
                        permission_id: permissionId,
                    }),
                });
            }

            console.log('Permisos actualizados');
            closeModal(); // Cerrar el modal después de actualizar
        } catch (error) {
            console.error('Error al actualizar permisos del usuario:', error.message);
        }
    };

    return (
        <div className="modal" style={{ display: 'block' }}>
            <div className="modal-content">
                <span className="close" onClick={closeModal}>&times;</span>
                <h2>Actualizar Permisos de Usuario</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label className="form-label">Permisos de Sistema:</label>
                        <div className="permissions-list">
                            {permissions.map((permission) => (
                                <div key={permission.id} className="permission-item">
                                    <label>
                                        <input
                                            type="checkbox"
                                            checked={selectedPermissions.includes(permission.id)}
                                            onChange={() => handlePermissionChange(permission.id)}
                                        />
                                        {permission.name}
                                    </label>
                                </div>
                            ))}
                        </div>
                    </div>
                    <button type="submit" className="submit-button">Actualizar</button>
                </form>
            </div>
        </div>
    );
};

export default UserModal;
