// src/context/AuthContext.js

import React, { createContext, useContext, useState } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [loggedIn, setLoggedIn] = useState(false); // Estado de autenticación
    const [role, setRole] = useState('user'); // Estado del rol del usuario (puede ser 'user' o 'admin')

    // Función para manejar el inicio de sesión
    const login = (role) => {
        setLoggedIn(true);
        setRole(role);
    };

    // Función para manejar el cierre de sesión
    const logout = () => {
        setLoggedIn(false);
        setRole('user');
        localStorage.removeItem('authToken'); // Limpiar token
        localStorage.removeItem('username'); // Limpiar nombre de usuario
        localStorage.removeItem('permissions'); // Limpiar permisos
    };

    return (
        <AuthContext.Provider value={{ loggedIn, role, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    return useContext(AuthContext);
};
