import React, { useState } from 'react';
import './UserPermissions.css';
import UserModal from './UserModal';

const UserPermissions = ({ users, roles, folders }) => {
    const [selectedUser, setSelectedUser] = useState(null);
    const [showModal, setShowModal] = useState(false);

    const openModal = (user) => {
        setSelectedUser(user);
        setShowModal(true);
    };

    const closeModal = () => {
        setSelectedUser(null);
        setShowModal(false);
    };

    return (
        <div className="config-container">
            <div className="config-header">
                <h1>Configuración</h1>
            </div>
            <div className="config-form">
                <h2>Gestión de Usuarios</h2>
                <table>
                    <thead>
                        <tr>
                            <th>Nombre Completo</th>
                            <th>Correo Electrónico</th>
                            <th>Estado</th>
                            <th>Permisos Asignados</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user) => (
                            <tr key={user.user.email}>
                                <td>{user.name} {user.lastname}</td>
                                <td>{user.email}</td>
                                <td>{user.status}</td>
                                <td>{user.permissions?.map(permission => permission.name).join(', ') || 'Sin permisos'}</td>


                                <td>
                                    <button className="edit-button" onClick={() => openModal(user)}>Editar</button>
                                    {/* Aquí se pueden añadir más botones para otras acciones */}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            {showModal && selectedUser && (
                <UserModal
                    user={selectedUser}
                    roles={roles}
                    folders={folders}
                    closeModal={closeModal}
                />
            )}
        </div>
    );
};

export default UserPermissions;
