// src/pages/UserConfig.js
import React, { useState, useEffect } from 'react';
import UserPermissions from './UserPermissions'; // Componente para manejar permisos
import ConfigForm from '../components/ConfigForm';
import { usePermissions } from '../context/PermissionsContext'; // Para verificar permisos
import './UserPermissions.css'; // Importa tu archivo CSS
import { useAuth } from '../context/AuthContext';

const UserConfig = () => {
    const [users, setUsers] = useState([]); // Estado para almacenar usuarios
    const { hasPermission } = usePermissions(); // Verifica si el usuario tiene permisos necesarios
    const { token } = useAuth(); // O desde el contexto de Auth si no usas localStorage

    useEffect(() => {
        // Función para obtener usuarios de la API
        const fetchUsers = async () => {
            try {
                const response = await fetch('http://fya-api.com:80/api/users', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                });

                if (!response.ok) {
                    throw new Error('Error al obtener usuarios');
                }

                const data = await response.json();
                setUsers(data.data); // Actualizar el estado de los usuarios
            } catch (error) {
                console.error('Error al obtener usuarios:', error.message);
                // Manejar error
            }
        };

        fetchUsers();
    }, [token]); // Dependencia de token para asegurarse de que esté disponible

    // Función para actualizar los datos de un usuario en el estado
    const updateUserPermissions = (updatedUser) => {
        setUsers(users.map(user => (user.user.id === updatedUser.user.id ? updatedUser : user)));
    };

    return (
        <div className="config-container">
            {hasPermission("Guardar Permisos") && (
                <UserPermissions users={users} updateUserPermissions={updateUserPermissions} /> // Pasar la función de actualización
            )}
            {/* Otras configuraciones */}
        </div>
    );
};

export default UserConfig;
