import React, { useState, useEffect } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { useAuth } from '../context/AuthContext';
import { usePermissions } from '../context/PermissionsContext';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const { login } = useAuth(); // Hook para manejar el inicio de sesión
    const { setPermissions } = usePermissions();
    const navigate = useNavigate(); // Hook para navegación

    useEffect(() => {
        document.body.classList.add('login-page');
        return () => {
            document.body.classList.remove('login-page');
        };
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://fya-api.com:80/api/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const data = await response.json();
            console.log('Datos recibidos del servidor:', data);

            if (response.ok) {
                const { token, user, permissions } = data.data;
                setPermissions(permissions); // Establece los permisos
                login(user.role, token, user.name); // Establece el rol, el token y el nombre de usuario en el contexto
                navigate('/dashboard'); // Redirigir al usuario al dashboard
            } else {
                throw new Error(data.message || 'Error al iniciar sesión');
            }
        } catch (error) {
            console.error('Error al iniciar sesión:', error.message);
            alert('Error al iniciar sesión, verifica tus credenciales');
        }
    };

    const handleGoogleSuccess = async (response) => {
        try {
            const idToken = response.credential;

            const result = await fetch('https://fya-api.com:8443/api/login/google', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ idToken }),
            });

            const data = await result.json();

            console.log('Datos recibidos del servidor (Google):', data);

            if (result.ok && data.data.token) {
                const { token, user, permissions } = data.data;
                setPermissions(permissions);
                login(user.role, token, user.name); // Establece el rol, el token y el nombre de usuario en el contexto
                navigate('/dashboard');
            } else {
                throw new Error('Error al autenticar con la API');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Error al iniciar sesión con Google');
        }
    };

    const handleGoogleFailure = (error) => {
        console.error('Error:', error);
        alert('Error al iniciar sesión con Google');
    };

    // Función para manejar la redirección a la página de recuperación de contraseña
    const [showPasswordRecovery, setShowPasswordRecovery] = useState(false);

    const handlePasswordRecovery = () => {
        setShowPasswordRecovery(true); // Muestra el formulario de recuperación
    };
    return (
        <GoogleOAuthProvider clientId="695490137144-2hsvgdbt5kg5r3pskedivcqpk670hk0sk.apps.googleusercontent.com">
            <div className="login-container">
                <img src="images/logo.webp" alt="Logo de la Organización" />
                <h1>Bienvenid@ al Portal</h1>
                <p>Inicio de Sesión</p>
                <form onSubmit={handleLogin}>
                    <input
                        type="email"
                        name="email"
                        placeholder="Correo electrónico"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        name="password"
                        placeholder="Contraseña"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <button type="submit">Iniciar Sesión</button>
                </form>
                <GoogleLogin
                    onSuccess={handleGoogleSuccess}
                    onFailure={handleGoogleFailure}
                />
                <p>
                <button
                                onClick={handlePasswordRecovery}
                                className="link-button"
                            >
                                ¿Olvidó su contraseña?
                            </button>
                </p>
            </div>
        </GoogleOAuthProvider>
    );
};

export default Login;
