import React, { useEffect } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { account } from '../appwrite';
import './Login.css';

const Login = () => {
  useEffect(() => {
    document.body.classList.add('login-page');
    return () => {
      document.body.classList.remove('login-page');
    };
  }, []);

  const handleGoogleSuccess = async (response) => {
    console.log(response);
    const token = response.credential;

    try {
      await account.createOAuth2Session('google', window.location.href, window.location.href);
      window.location.href = '/dashboard';
    } catch (error) {
      console.error('Error:', error);
      alert('Error al iniciar sesión con Google');
    }
  };

  const handleGoogleFailure = (error) => {
    console.error('Error:', error);
    alert('Error al iniciar sesión con Google');
  };

  return (
    <GoogleOAuthProvider clientId="695490137144-2hsvgdbt5kg5r3pskedivcqpk670hk0sk.apps.googleusercontent.com">
      <div className="login-container">
        <img src="images/logo.webp" alt="Logo de la Organización" />
        <h1>Bienvenid@ al Portal</h1>
        <p>Inicio de Sesión</p>
        <GoogleLogin
          onSuccess={handleGoogleSuccess}
          onFailure={handleGoogleFailure}
        />
        <p>
          <button
            onClick={() => alert('Funcionalidad no implementada aún')}
            className="link-button"
          >
            ¿Olvidó su contraseña?
          </button>
        </p>
      </div>
    </GoogleOAuthProvider>
  );
};

export default Login;
