// src/pages/UserConfig.js

import React from 'react';
import UserPermissions from './UserPermissions';
import ConfigForm from '../components/ConfigForm';
import { usePermissions } from '../context/PermissionsContext'; // Importa el contexto de permisos

const users = [
    { username: 'john_doe', role: 'admin', folders: ['Finance', 'HR'] },
    { username: 'jane_smith', role: 'user', folders: ['Marketing'] },
];

const roles = ['admin', 'user'];
const folders = ['Finance', 'HR', 'Marketing'];

const UserConfig = () => {
    const { hasPermission } = usePermissions(); // Usa el hook para verificar permisos

    return (
        <div className="config-container">
            {hasPermission("Guardar Permisos") && ( // Verifica si el usuario tiene el permiso necesario
                <UserPermissions users={users} roles={roles} folders={folders} />
            )}
            <ConfigForm
                title="Configuración de Respaldo Automático"
                action="/api/backup"
                fields={[
                    { id: 'frecuencia', label: 'Frecuencia de Respaldo (días):', type: 'number', name: 'frecuencia', min: '1', required: true }
                ]}
            />
            <ConfigForm
                title="Eliminar Registros de Auditoría"
                action="/api/delete_audit"
                fields={[
                    { id: 'start_date', label: 'Fecha de Inicio:', type: 'date', name: 'start_date', required: true },
                    { id: 'end_date', label: 'Fecha de Fin:', type: 'date', name: 'end_date', required: true }
                ]}
            />
            <ConfigForm
                title="Eliminar Registros de Respaldo"
                action="/api/delete_backup"
                fields={[
                    { id: 'start_date', label: 'Fecha de Inicio:', type: 'date', name: 'start_date', required: true },
                    { id: 'end_date', label: 'Fecha de Fin:', type: 'date', name: 'end_date', required: true }
                ]}
            />
        </div>
    );
};

export default UserConfig;
