import React, { useState, useEffect } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { usePermissions } from '../context/PermissionsContext'; // Importar el hook de permisos
import './Login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const { setPermissions } = usePermissions(); // Usar el hook para actualizar permisos en el contexto

    useEffect(() => {
        document.body.classList.add('login-page');
        return () => {
            document.body.classList.remove('login-page');
        };
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://fya-api.com:80/api/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const data = await response.json();
            console.log('Datos recibidos del servidor:', data);

            if (response.ok) {
                const { token, user, permissions } = data.data; // Extraer token, usuario y permisos
                localStorage.setItem('authToken', token); // Solo el token se guarda en localStorage
                localStorage.setItem('username', user.name); // Guardar el nombre del usuario
                setPermissions(permissions); // Actualizar permisos en el contexto
                console.log('Token guardado en localStorage:', token);
                console.log('Nombre de usuario guardado en localStorage:', user.name);
                console.log('Permisos establecidos en el contexto:', permissions);
                window.location.href = '/dashboard';
            } else {
                throw new Error(data.message || 'Error al iniciar sesión');
            }
        } catch (error) {
            console.error('Error al iniciar sesión:', error.message);
            alert('Error al iniciar sesión, verifica tus credenciales');
        }
    };

    const handleGoogleSuccess = async (response) => {
        try {
            const idToken = response.credential; // Extraer el token de Google

            const result = await fetch('https://fya-api.com:8443/api/login/google', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ idToken }),
            });

            const data = await result.json();

            console.log('Datos recibidos del servidor (Google):', data);

            if (result.ok && data.data.token) {
                const { token, user, permissions } = data.data;
                localStorage.setItem('authToken', token); // Solo el token se guarda en localStorage
                localStorage.setItem('username', user.name);
                setPermissions(permissions); // Actualizar permisos en el contexto
                console.log('Token de Google guardado en localStorage:', token);
                console.log('Nombre de usuario de Google guardado en localStorage:', user.name);
                console.log('Permisos establecidos en el contexto (Google):', permissions);
                window.location.href = '/dashboard';
            } else {
                throw new Error('Error al autenticar con la API');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Error al iniciar sesión con Google');
        }
    };

    const handleGoogleFailure = (error) => {
        console.error('Error:', error);
        alert('Error al iniciar sesión con Google');
    };

    return (
        <GoogleOAuthProvider clientId="695490137144-2hsvgdbt5kg5r3pskedivcqpk670hk0sk.apps.googleusercontent.com">
            <div className="login-container">
                <img src="images/logo.webp" alt="Logo de la Organización" />
                <h1>Bienvenid@ al Portal</h1>
                <p>Inicio de Sesión</p>
                <form onSubmit={handleLogin}>
                    <input
                        type="email"
                        name="email"
                        placeholder="Correo electrónico"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        name="password"
                        placeholder="Contraseña"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <button type="submit">Iniciar Sesión</button>
                </form>
                <GoogleLogin
                    onSuccess={handleGoogleSuccess}
                    onFailure={handleGoogleFailure}
                />
                <p>
                    <button
                        onClick={() => alert('Funcionalidad no implementada aún')}
                        className="link-button"
                    >
                        ¿Olvidó su contraseña?
                    </button>
                </p>
            </div>
        </GoogleOAuthProvider>
    );
};

export default Login;
