import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css'; // Asegúrate de incluir el archivo CSS para el diseño

const ResetPassword = ({ token, email }) => {
    const [password, setPassword] = useState('');
    const [passwordConfirmation, setPasswordConfirmation] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formData = {
            token,
            email,
            password,
            password_confirmation: passwordConfirmation
        };

        try {
            const response = await fetch('https://fya-api.com:8443/api/password-reset', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            const data = await response.json();

            if (response.ok) {
                setMessage(data.message || 'Contraseña restablecida exitosamente.');
                // Aquí puedes redirigir al login después de 3 segundos
                setTimeout(() => navigate('/login'), 3000);
            } else if (response.status === 419) {
                setMessage('El token ha expirado. Por favor, solicita un nuevo enlace.');
            } else {
                setMessage(data.message || 'Ocurrió un error. Intenta nuevamente.');
            }
        } catch (error) {
            console.error('Error:', error);
            setMessage('Ocurrió un error. Intenta nuevamente.');
        }
    };

    return (
        <div className="reset-password-container">
            <h1>Restablecer Contraseña</h1>
            {message && <p className="message">{message}</p>}
            <form onSubmit={handleSubmit} className="reset-password-form">
                <input
                    type="hidden"
                    name="token"
                    value={token}
                />
                <input
                    type="hidden"
                    name="email"
                    value={email}
                />
                <label htmlFor="password">Nueva Contraseña:</label>
                <input
                    type="password"
                    name="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />

                <label htmlFor="password_confirmation">Confirmar Nueva Contraseña:</label>
                <input
                    type="password"
                    name="password_confirmation"
                    value={passwordConfirmation}
                    onChange={(e) => setPasswordConfirmation(e.target.value)}
                    required
                />

                <button type="submit">Restablecer Contraseña</button>
            </form>
        </div>
    );
};

export default ResetPassword;
