// src/pages/UserConfig.js

import React, { useState, useEffect } from 'react';
import UserPermissions from './UserPermissions'; // Suponiendo que tienes un componente para manejar permisos
import ConfigForm from '../components/ConfigForm';
import { usePermissions } from '../context/PermissionsContext'; // Para verificar permisos
import './UserPermissions.css'; // Importa tu archivo CSS

const UserConfig = () => {
    const [users, setUsers] = useState([]); // Estado para almacenar usuarios
    const { hasPermission } = usePermissions(); // Verifica si el usuario tiene permisos necesarios

    useEffect(() => {
        // Función para obtener usuarios de la API
        const fetchUsers = async () => {
            try {
                const token = localStorage.getItem('authToken');
                console.log('Token de autenticación:', token); // Imprimir el token

                const response = await fetch('/api/users', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                });

                console.log('Estado de respuesta:', response.status); // Imprimir el estado de la respuesta
                console.log('Encabezados de respuesta:', response.headers); // Imprimir los encabezados de la respuesta

                if (!response.ok) {
                    const errorText = await response.text();
                    console.error('Error de respuesta del servidor:', errorText); // Imprimir error del servidor
                    throw new Error('Error al obtener usuarios');
                }

                const contentType = response.headers.get("content-type");
                if (contentType && contentType.indexOf("application/json") !== -1) {
                    const data = await response.json();
                    console.log('Datos recibidos:', data); // Imprimir los datos recibidos
                    const formattedUsers = data.data.map(userData => ({
                        username: userData.user.name,
                        role: userData.user.role || 'user', // Asigna 'user' si no se especifica un rol
                        folders: userData.user.permissions.map(permission => permission.name),
                    }));

                    setUsers(formattedUsers); // Actualiza el estado con los usuarios obtenidos
                } else {
                    const text = await response.text();
                    console.error('Respuesta inesperada:', text); // Imprimir respuesta inesperada
                    throw new Error('La respuesta no es un JSON válido');
                }

            } catch (error) {
                console.error('Error al obtener usuarios:', error.message); // Imprimir error al obtener usuarios
            }
        };

        fetchUsers();
    }, []); // El arreglo vacío asegura que esto se ejecute solo una vez al montar el componente

    return (
        <div className="config-container">
            {hasPermission("Guardar Permisos") && (
                <UserPermissions users={users} /> // Renderiza solo si tiene permiso
            )}
            <ConfigForm
                title="Configuración de Respaldo Automático"
                action="/api/backup"
                fields={[
                    { id: 'frecuencia', label: 'Frecuencia de Respaldo (días):', type: 'number', name: 'frecuencia', min: '1', required: true }
                ]}
            />
            <ConfigForm
                title="Eliminar Registros de Auditoría"
                action="/api/delete_audit"
                fields={[
                    { id: 'start_date', label: 'Fecha de Inicio:', type: 'date', name: 'start_date', required: true },
                    { id: 'end_date', label: 'Fecha de Fin:', type: 'date', name: 'end_date', required: true }
                ]}
            />
            <ConfigForm
                title="Eliminar Registros de Respaldo"
                action="/api/delete_backup"
                fields={[
                    { id: 'start_date', label: 'Fecha de Inicio:', type: 'date', name: 'start_date', required: true },
                    { id: 'end_date', label: 'Fecha de Fin:', type: 'date', name: 'end_date', required: true }
                ]}
            />
        </div>
    );
};

export default UserConfig;
