// src/context/AuthContext.js
import React, { createContext, useContext, useState } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [loggedIn, setLoggedIn] = useState(false);
    const [role, setRole] = useState('user'); // 'user' es el valor por defecto

    // Función para manejar el inicio de sesión
    const login = (userRole) => {
        setLoggedIn(true);
        setRole(userRole); // Establece el rol basado en la autenticación
    };

    // Función para manejar el cierre de sesión
    const logout = () => {
        setLoggedIn(false);
        setRole('user'); // Restablece el rol a 'user' o cualquier valor predeterminado
        // Limpiar cualquier otra información de sesión si es necesario
    };

    return (
        <AuthContext.Provider value={{ loggedIn, role, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
