import React, { useEffect } from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { account } from '../appwrite';
import './Login.css';

const Login = () => {
  useEffect(() => {
    // Añade una clase específica al body cuando el componente de login se monta
    document.body.classList.add('login-page');

    // Remueve la clase específica del body cuando el componente de login se desmonta
    return () => {
      document.body.classList.remove('login-page');
    };
  }, []);

  const handleGoogleSuccess = async (response) => {
    console.log(response);
    try {
      // Usa la sesión OAuth2 de Appwrite para iniciar sesión con Google
      await account.createOAuth2Session('google', 'http://localhost:3000/dashboard', 'http://localhost:3000/login');
    } catch (error) {
      console.error('Error:', error);
      alert('Error al iniciar sesión con Google');
    }
  };

  const handleGoogleFailure = (error) => {
    console.error('Error:', error);
    alert('Error al iniciar sesión con Google');
  };

  return (
    <GoogleOAuthProvider clientId="695490137144-2hsvgdbt5kg5r3pskedivcqpk670hk0sk.apps.googleusercontent.com">
      <div className="login-container">
        <img src="images/logo.webp" alt="Logo de la Organización" />
        <h1>Bienvenid@ al Portal</h1>
        <p>Inicio de Sesión</p>
        <form method="POST" action="/login">
          <input type="text" name="username" placeholder="Usuario" required />
          <input type="password" name="password" placeholder="Contraseña" required />
          <button type="submit">Iniciar Sesión</button>
        </form>
        <GoogleLogin
          onSuccess={handleGoogleSuccess}
          onFailure={handleGoogleFailure}
        />
        <p>
          <button
            onClick={() => alert('Funcionalidad no implementada aún')}
            className="link-button"
          >
            ¿Olvidó su contraseña?
          </button>
        </p>
      </div>
    </GoogleOAuthProvider>
  );
};

export default Login;
