// src/components/BaseLayout.js
import React from 'react';
import Header from './Header';
import Footer from './Footer';
import 'https://fonts.googleapis.com/css2?family=Raleway:wght@400;700&display=swap';
import 'https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const BaseLayout = ({ children }) => {
  // Simulación de mensajes flash (puedes reemplazar esto con la lógica real de tu aplicación)
  const messages = ['Message 1', 'Message 2'];

  return (
    <div style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      <Header />
      <div className="container">
        {messages.map((message, index) => (
          toast.info(message)
        ))}
      </div>
      <div className="content" style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        {children}
      </div>
      <Footer className="footer" />
      <ToastContainer />
    </div>
  );
};
