// src/components/UserModal.js
import React, { useState, useEffect } from 'react';
import './UserPermissions.css';
import { useAuth } from '../context/AuthContext';

const UserModal = ({ user, permissions = [], roles = [], closeModal }) => {
    const { token } = useAuth();

    // Aseguramos que 'permissions' y 'roles' son arrays
    permissions = Array.isArray(permissions) ? permissions : [];
    roles = Array.isArray(roles) ? roles : [];

    const [selectedPermissions, setSelectedPermissions] = useState([]);
    const [selectedRoles, setSelectedRoles] = useState([]);

    useEffect(() => {
        // Actualiza el estado si el usuario cambia
        setSelectedPermissions(user.permissions?.map(permission => permission.id) || []);
        setSelectedRoles(user.roles?.map(role => role.id) || []);
    }, [user]);

    const handlePermissionChange = (permissionId) => {
        if (selectedPermissions.includes(permissionId)) {
            setSelectedPermissions(selectedPermissions.filter(id => id !== permissionId));
        } else {
            setSelectedPermissions([...selectedPermissions, permissionId]);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const currentPermissions = user.permissions.map(permission => permission.id);

            // Permisos para asignar
            const permissionsToAssign = selectedPermissions.filter(id => !currentPermissions.includes(id));
            // Permisos para revocar
            const permissionsToRevoke = currentPermissions.filter(id => !selectedPermissions.includes(id));

            // Realizar peticiones para asignar permisos
            for (const permissionId of permissionsToAssign) {
                await fetch('http://fya-api.com:80/api/assign-permission', {
                    method: 'POST',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        user_id: user.id,
                        permission_id: permissionId,
                    }),
                });
            }

            // Realizar peticiones para revocar permisos
            for (const permissionId of permissionsToRevoke) {
                await fetch('http://fya-api.com:80/api/revoke-permission', {
                    method: 'POST',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        user_id: user.id,
                        permission_id: permissionId,
                    }),
                });
            }

            // Actualizar el rol del usuario si ha cambiado
            const currentRoleId = user.roles?.[0]?.id || null;
            if (selectedRoles[0] !== currentRoleId) {
                await fetch('http://fya-api.com:80/api/update-user-role', {
                    method: 'POST',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        user_id: user.id,
                        role_id: selectedRoles[0],
                    }),
                });
            }

            closeModal(); // Cerrar el modal después de actualizar
        } catch (error) {
            console.error('Error al actualizar permisos o rol del usuario:', error.message);
        }
    };

    return (
        <div className="modal" style={{ display: 'block' }}>
            <div className="modal-content">
                <span className="close" onClick={closeModal}>&times;</span>
                <h2>Actualizar Permisos y Rol de Usuario</h2>
                <form onSubmit={handleSubmit}>
                    {/* Campo de selección de roles */}
                    <div className="form-group">
                        <label className="form-label">Rol:</label>
                        <select
                            value={selectedRoles[0] || ''}
                            onChange={(e) => setSelectedRoles([parseInt(e.target.value)])}
                            className="form-control"
                        >
                            <option value="" disabled>Seleccione un rol</option>
                            {roles.map((role) => (
                                <option key={role.id} value={role.id}>
                                    {role.name}
                                </option>
                            ))}
                        </select>
                    </div>
                    {/* Lista de permisos */}
                    <div className="form-group">
                        <label className="form-label">Permisos de Sistema:</label>
                        <div className="permissions-list">
                            {permissions.map((permission) => (
                                <div key={permission.id} className="permission-item">
                                    <label>
                                        <input
                                            type="checkbox"
                                            checked={selectedPermissions.includes(permission.id)}
                                            onChange={() => handlePermissionChange(permission.id)}
                                        />
                                        {permission.name}
                                    </label>
                                </div>
                            ))}
                        </div>
                    </div>
                    <button type="submit" className="submit-button">Actualizar</button>
                </form>
            </div>
        </div>
    );
};

export default UserModal;
