import React, { useState, useEffect } from 'react';
import './UserPermissions.css';

const UserModal = ({ user, permissions = [], closeModal }) => {
    const [selectedPermissions, setSelectedPermissions] = useState(user?.permissions?.map(permission => permission.id) || []);

    useEffect(() => {
        // Actualiza el estado si el usuario cambia
        setSelectedPermissions(user?.permissions?.map(permission => permission.id) || []);
    }, [user]);

    const handlePermissionChange = (e) => {
        const value = Array.from(
            e.target.selectedOptions,
            (option) => option.value
        );
        setSelectedPermissions(value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        // Aquí puedes implementar la lógica para actualizar los permisos del usuario
        try {
            const response = await fetch('/api/updateUserPermissions', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    username: user.username,
                    permissions: selectedPermissions,
                }),
            });

            const data = await response.json();
            if (response.ok) {
                console.log('Usuario actualizado:', data);
                closeModal(); // Cerrar el modal después de actualizar
            } else {
                console.error('Error al actualizar usuario:', data.message);
            }
        } catch (error) {
            console.error('Error al actualizar usuario:', error.message);
        }
    };

    return (
        <div className="modal" style={{ display: 'block' }}>
            <div className="modal-content">
                <span className="close" onClick={closeModal}>&times;</span>
                <h2>Actualizar Permisos de Usuario</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="permissions" className="form-label">Permisos de Sistema:</label>
                        <select
                            id="permissions"
                            name="permissions"
                            className="form-control"
                            multiple
                            value={selectedPermissions}
                            onChange={handlePermissionChange}
                        >
                            {permissions.map((permission) => (
                                <option key={permission.id} value={permission.id}>
                                    {permission.name}
                                </option>
                            ))}
                        </select>
                    </div>
                    <button type="submit" className="submit-button">Actualizar</button>
                </form>
            </div>
        </div>
    );
};

export default UserModal;
