// src/components/UserPermissions.js
import React, { useState, useEffect } from 'react';
import './UserPermissions.css';
import UserModal from './UserModal';
import { useAuth } from '../context/AuthContext';

const UserPermissions = ({ users, roles, folders }) => {
    const [permissions, setPermissions] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const { token } = useAuth();

    useEffect(() => {
        const fetchPermissions = async () => {
            try {
                const response = await fetch('http://fya-api.com:80/api/permissions', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                });
                const data = await response.json();
                if (response.ok) {
                    setPermissions(data.data);
                } else {
                    console.error('Error al obtener permisos:', data.message);
                }
            } catch (error) {
                console.error('Error al obtener permisos:', error.message);
            }
        };

        fetchPermissions();
    }, [token]);

    const openModal = (user) => {
        setSelectedUser(user);
        setShowModal(true);
    };

    const closeModal = () => {
        setSelectedUser(null);
        setShowModal(false);
    };

    // Función para actualizar los permisos de un usuario
    const updateUserPermissions = (updatedUser) => {
        setUsers((prevUsers) =>
            prevUsers.map((user) => (user.user.id === updatedUser.user.id ? updatedUser : user))
        );
    };

    return (
        <div className="config-container">
            <div className="config-header">
                <h1>Configuración</h1>
            </div>
            <div className="config-form">
                <h2>Gestión de Usuarios</h2>
                <table>
                    <thead>
                        <tr>
                            <th>Nombre Completo</th>
                            <th>Correo Electrónico</th>
                            <th>Estado</th>
                            <th>Permisos Asignados</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user) => (
                            <tr key={user.user.email}>
                                <td>{user.user.name} {user.user.lastname}</td>
                                <td>{user.user.email}</td>
                                <td>{user.user.status}</td>
                                <td>{user.user.permissions?.map(permission => permission.name).join(', ') || 'Sin permisos'}</td>
                                <td>
                                    <button className="edit-button" onClick={() => openModal(user)}>Editar</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            {showModal && selectedUser && (
                <UserModal
                    user={selectedUser}
                    roles={roles}
                    permissions={permissions}
                    closeModal={closeModal}
                    updateUserPermissions={updateUserPermissions} // Pasar la función de actualización
                />
            )}
        </div>
    );
};

export default UserPermissions;
