// src/pages/Login.js
import React, { useEffect } from 'react';
import './Login.css';

const Login = () => {
  useEffect(() => {
    // Añade una clase específica al body cuando el componente de login se monta
    document.body.classList.add('login-page');

    // Remueve la clase específica del body cuando el componente de login se desmonta
    return () => {
      document.body.classList.remove('login-page');
    };
  }, []);

  return (
    <div className="login-container">
      <img src="images/logo.webp" alt="Logo de la Organización" />
      <h1>Bienvenid@ al Portal</h1>
      <p>Inicio de Sesión</p>
      <form method="POST" action="/login">
        <input type="text" name="username" placeholder="Usuario" required />
        <input type="password" name="password" placeholder="Contraseña" required />
        <button type="submit">Iniciar Sesión</button>
      </form>
      <div
        id="g_id_onload"
        data-client_id="YOUR_GOOGLE_CLIENT_ID"
        data-login_uri="https://your.domain/your_login_endpoint"
        data-auto_prompt="false"
      ></div>
      <div
        className="g_id_signin"
        data-type="standard"
        data-size="large"
        data-theme="outline"
        data-text="sign_in_with"
        data-shape="rectangular"
        data-logo_alignment="left"
      ></div>
      <p><a href="#">¿Olvidó su contraseña?</a></p>
    </div>
  );
};

export default Login;
