// src/components/UserModal.js
import React, { useState, useEffect } from 'react';
import './UserPermissions.css';

const UserModal = ({ user, roles, folders, closeModal }) => {
    const [role, setRole] = useState(user.role);
    const [selectedFolders, setSelectedFolders] = useState(user.folders);

    const handleFolderChange = (e) => {
        const value = Array.from(
            e.target.selectedOptions,
            (option) => option.value
        );
        setSelectedFolders(value);
    };

    return (
        <div className="modal" style={{ display: 'block' }}>
            <div className="modal-content">
                <span className="close" onClick={closeModal}>&times;</span>
                <h2>Actualizar Rol y Permisos de Usuario</h2>
                <form method="POST" action="/api/updateUserPermissions">
                    <input type="hidden" name="username" value={user.username} />
                    <div className="form-group">
                        <label htmlFor="role" className="form-label">Rol:</label>
                        <select
                            id="role"
                            name="role"
                            className="form-control"
                            value={role}
                            onChange={(e) => setRole(e.target.value)}
                        >
                            {roles.map((role) => (
                                <option key={role} value={role}>{role}</option>
                            ))}
                        </select>
                    </div>
                    <div className="form-group">
                        <label htmlFor="folders" className="form-label">Permisos de Carpetas:</label>
                        <select
                            id="folders"
                            name="folders"
                            className="form-control"
                            multiple
                            value={selectedFolders}
                            onChange={handleFolderChange}
                        >
                            {folders.map((folder) => (
                                <option key={folder} value={folder}>{folder}</option>
                            ))}
                        </select>
                    </div>
                    <button type="submit" className="submit-button">Actualizar</button>
                </form>
            </div>
        </div>
    );
};

export default UserModal;
